-- Verificar a importação
SELECT 
    id, 
    name, 
    street, 
    neighborhood, 
    status,
    CASE 
        WHEN history != '[]' THEN 'Com histórico'
        ELSE 'Sem histórico'
    END as tem_historico
FROM areas 
WHERE id BETWEEN 885 AND 547
ORDER BY id;

-- Contar total de áreas importadas no lote 3
SELECT 
    COUNT(*) as total_importado,
    COUNT(CASE WHEN status = 'Concluído' THEN 1 END) as concluidas,
    COUNT(CASE WHEN status = 'Pendente' THEN 1 END) as pendentes,
    COUNT(CASE WHEN history != '[]' THEN 1 END) as com_historico
FROM areas 
WHERE id BETWEEN 885 AND 547;

